INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('32589406-1004-49b8-86c3-421954b9d316', 'Türkiyede Çocuk ve Eğitimi', 'Fatma Avcı, Öznur Ateş, Ekrem Cengiz Akdeniz', '9786256658226', 'EĞİTİM YAYINEVİ', 2024, 332, 'Genel', 'Çocuk koşmalı, Çocuk oyun oynamalı, Özgür olmalı çocuk, Hayaller kurabilmeli, Güvende olduğunu bilmeli, Gülümsemesi eksik olmamalı hiçbir çocuğun, Her çocuk doyasıya yaşayabilmeli hayatı. Türkiye’de Çocuk ve Eğitimi başlıklı kitabımız öğretmen adaylarına, öğretmenlere, okul yöneticilerine ve akademisyenlere sunulmaktadır.', 'http://books.google.com/books/content?id=SCUHEQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-05-27T17:34:18.936Z', '2025-05-27T17:34:18.936Z') ON CONFLICT (id) DO NOTHING;
INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('3e2aee79-c4c4-4e53-bb73-fb3133ef8f3f', 'Araba Sevdası (Günümüz Türkçesiyle)', 'Recaizade Mahmut Ekrem', '9789750720420', 'Can Yayınları', 2022, 240, 'Genel', '“Pek az Türk romanı Araba Sevdası kadar adına bağlıdır. Kitap, bir modanın ve muayyen iktisadi şartlar etrafında hemen bir lahzada teşekkül etmiş köksüz bir kalabalığın romanıdır.” Ahmet Hamdi Tanpınar Tanzimat edebiyatının birinci döneminde temelleri atılan modern edebiyatımızın duvarlarını örmeye başlayan isimlerden biri de Recaizade Mahmut Ekrem’dir. Gençleri yazmaya, yazdıklarını yayımlatmaya teşvik ederek bir neslin önünü açan üstat Recaizade’nin Araba Sevdası romanı, Türkçede roman türünün başarılı ilk örneklerinden biri olarak kabul edilir. Edebiyatımızda sıkça işlenen mirasyedi, züppe tiplerin öncülerinden olan Bihruz Bey’in hayatı, düşünceleri ve aşkı, Araba Sevdası’nda yer yer komedileştirilerek anlatılırken okura o dönemi ve ülkenin Batılılaşma macerasını enine boyuna düşünme imkânı sunar. 1896’da Servet-i Fünûn’da tefrika edilen bu büyük romanı, orijinalinde yer alan resimler, döneme dair diğer fotoğraflar ve dipnotlarla zenginleştirilmiş bir baskıyla, günümüz Türkçesine uyarlayarak sunuyoruz. #edebiyatımızınmirası #tanzimatedebiyatı #züppelik #çamlıca #beyoğlu #konakhayatı #aşkacısı', 'http://books.google.com/books/content?id=0jo7CgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-05-27T17:45:05.851Z', '2025-05-27T17:45:05.851Z') ON CONFLICT (id) DO NOTHING;
INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('15c7fb1b-9b97-44b7-94d7-c82905655c75', 'Kitap Kokusu', 'Mustafa Kemal Erdemol', '9789750737077', 'Can Yayınları', 2020, 209, 'Genel', 'Deneyimli gazeteci Mustafa K. Erdemol daha çocukluğunda kokusuna kapılmış kitapların. Büyüdükçe de dört yanını sarmış bu koku, koyu bir okuma tutkusuyla her fırsatta kitapların dünyasına eğilmiş. İyi okur olmanın peşinde, yıllar boyunca tuttuğu notların, biriktirdiği tarihî anekdotların, bizzat kendi tatlı hatıralarının kendine has karışımlarından mürekkep denemeler yer alıyor Kitap Kokusu’nda. Bir nevi kırkambar: Türk ve dünya tarihinden, Doğu ve Batı kültüründen, zamanın farklı noktalarından hepimizin aşina olduğu ya da bazılarımızı şaşırtacak pek çok detay akıp gidiyor sayfalarda. Anlık bir kokunun cezbettiği, aşçıları olduğu kadar oburları da doyuracak, mutlu edecek bir kitap. Yazılı kâğıdın neden güzel koktuğunu, o kâğıdı koklamış olmanın bana neden zevk verdiğini hâlâ bilmiyorum. Ama bunun bir bağımlılık olduğunu kabul ediyorum. Matbaa, mürekkep, kâğıt kokusunu sevmenin ne olduğunu herkes anlamayabilir.', 'http://books.google.com/books/content?id=eAj5DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-06-01T09:23:36.179Z', '2025-06-01T09:23:36.179Z') ON CONFLICT (id) DO NOTHING;
INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('3030e1a8-2bba-4b72-a58e-3facb44586e1', 'Aşk İncileri (Fihi Ma Fih)', 'Yrd. Doç. Dr. Veysel Akkaya', '9786053021353', 'ERKAM YAYIN SAN. A.Ş.', NULL, 162, 'Genel', '"Aşk İncileri (Fihi Ma Fih) Yayınevi: ERKAM YAYINLARI Yazar: Yrd. Doç.Dr.Veysel AKKAYA Aşk İncileri (Fihi Ma Fih) Aşktır denizi tencere gibi kaynataN Aşktır dağı ezen kum gibi yapan Aşktır göğü yüz yerden yaran Aşktır arza vasıtasız damlayan MEVLÂNÂ Hak aşığı mevlana celaleddin-i rumi, rasûlüllah aleyhissalatü vesselam''dan aldığı ilahi aşk incilerini bütün insanlığın üzerine saçtı.Bu sebeple dünyanın her yerinde insanlar hangi kültür ve medeniyete sahip olursa olsunlar, O''nun aşk dolu sözlerinde kendilerinden çok şey buldular.Açmış olduğu Muhammedi kucağa koştular.Engin gönlünden kaynayan ilahi pınarın sularından kana kana içtiler... "', 'http://books.google.com/books/content?id=YCVZDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-06-01T10:45:04.289Z', '2025-06-01T10:45:04.289Z') ON CONFLICT (id) DO NOTHING;
INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('46c7d327-f5d8-44a9-885c-d8c155118d55', 'Harry Potter ve Sırlar Odası – 2', 'J.K. Rowling', '9789750837593', 'Yapı Kredi Yayınları', 2022, 9, 'Genel', 'Sırlar Odası Açıldı. Vârisin Düşmanları, Kendinizi Kollayın. Küçük sefil ev cini Dobby, Privet Drive’da bir anda ortaya çıkıp Harry Potter’ın Hogwarts’a kesinlikle geri dönmemesi için ısrar edince Harry, bunun ardında ezeli düşmanı Draco Malfoy’un olabileceğinden şüphelenir. Ron Weasley’nin korkunç Dursley’lerden kurtardığı –sihirli bir uçan arabayla– Harry, yazın kalan günlerini Kovuk’ta geçirir. Weasley’lerle yaşam dikkat dağıtıcı bir sürü sihirli şeyle doludur, Harry kısa sürede Dobby’nin çılgın uyarılarını unutur. Ama okulda karanlık bir koridorda Dobby’nin korkunç şeyler olacağına dair öngörülerini yineleyen korkunç bir mesaj bulunur… J.K. Rowling’in klasikleşen serisinin ikinci kitabının bu resimli özel baskısında, Kate Greenaway Madalyası sahibi Jim Kay, büyücüler dünyasını büyülü ayrıntılarla canlandırıyor… Hem hayranlar hem de yeni okurlar için mükemmel.', 'http://books.google.com/books/content?id=UaxYEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-06-01T11:36:45.476Z', '2025-06-01T11:36:45.476Z') ON CONFLICT (id) DO NOTHING;
