INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('f3404f9f-c935-404c-b761-5a506cccc223', 'The Ivory Tower and Harry Potter', 'Lana A. Whited', '0826215491', 'University of Missouri Press', 2004, 436, 'Genel', 'Now available in paper, The Ivory Tower and Harry Potter is the first book-length analysis of J. K. Rowling''s work from a broad range of perspectives within literature, folklore, psychology, sociology, and popular culture. A significant portion of the book explores the Harry Potter series'' literary ancestors, including magic and fantasy works by Ursula K. LeGuin, Monica Furlong, Jill Murphy, and others, as well as previous works about the British boarding school experience. Other chapters explore the moral and ethical dimensions of Harry''s world, including objections to the series raised within some religious circles. In her new epilogue, Lana A. Whited brings this volume up to date by covering Rowling''s latest book, Harry Potter and the Order of the Phoenix.', 'http://books.google.com/books/content?id=iO5pApw2JycC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-06-01T12:05:00.001Z', '2025-06-01T12:05:00.001Z') ON CONFLICT (id) DO NOTHING;
INSERT INTO books (id, title, author, isbn, publisher, "publishedYear", "pageCount", genre, description, cover_image_url, language, "createdAt", "updatedAt") VALUES ('67b084a4-6b37-4723-ba59-3e35e4fd9bb1', 'The Magic of Harry Potter', 'Daniel Mitchell', '9780615172828', 'Lulu.com', 2007, 109, 'Genel', 'The Harry Potter books by J. K. Rowling have been phenomenal bestsellers winning fans world wide. These essays look at the magic, literary devices and moral themes in the Harry Potter series. In addition, the controversy between Christians over the moral themes is discussed.', 'http://books.google.com/books/content?id=c_KYSDoCYQ4C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'tr', '2025-06-01T12:26:12.503Z', '2025-06-01T12:26:12.503Z') ON CONFLICT (id) DO NOTHING;
